          (function(d, s, id) {  var js, fjs = d.getElementsByTagName(s)[0];  if (d.getElementById(id)) return;  js = d.createElement(s); js.id = id;  js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.3";  fjs.parentNode.insertBefore(js, fjs);}(document, 'script', 'facebook-jssdk'));
         </script>
         <div class="fb-video fb_iframe_widget fb_iframe_widget_fluid_desktop" data-allowfullscreen="true" data-href="//www.facebook.com/video.php?v=10156280307485364" data-width="600" fb-iframe-plugin-query="allowfullscreen=true&amp;app_id=&amp;container_width=900&amp;href=https%3A%2F%2Fwww.facebook.com%2Fvideo.php%3Fv%3D10156280307485364&amp;locale=en_US&amp;sdk=joey&amp;width=600" fb-xfbml-state="rendered">
          <span style="vertical-align: bottom; width: 600px; height: 123px;">
           <iframe allow="encrypted-media" allowfullscreen="true" allowtransparency="true" class="" data-testid="fb:video Facebook Social Plugin" frameborder="0" height="1000px" name="f6ab637fd7362" scrolling="no" src="https://web.facebook.com/v2.3/plugins/video.php?allowfullscreen=true&amp;app_id=&amp;channel=https%3A%2F%2Fstaticxx.facebook.com%2Fx%2Fconnect%2Fxd_arbiter%2F%3Fversion%3D46%23cb%3Df3f30e87aa5ae98%26domain%3Dchristmastrees.co.nz%26is_canvas%3Dfalse%26origin%3Dhttps%253A%252F%252Fchristmastrees.co.nz%252Ff6700cbb2b95b8%26relation%3Dparent.parent&amp;container_width=900&amp;href=https%3A%2F%2Fwww.facebook.com%2Fvideo.php%3Fv%3D10156280307485364&amp;locale=en_US&amp;sdk=joey&amp;width=600" style="border: none; visibility: visible; width: 600px; height: 123px;" title="fb:video Facebook Social Plugin" width="600px">
